import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || {};

  const [errorIndex, setErrorIndex] = useState(0);
  const [errorList, setErrorList] = useState([]);
  const [manualEdit, setManualEdit] = useState("");
  const [editedSentences, setEditedSentences] = useState({}); // tracks edits

  useEffect(() => {
    const jsonString = localStorage.getItem("uploadedJson");
    if (jsonString) {
      const json = JSON.parse(jsonString);
      const list = [];

      Object.entries(json).forEach(([sentence, errors]) => {
        errors.forEach(([start, end, suggestions]) => {
          list.push({
            originalSentence: sentence,
            start,
            end,
            suggestions,
            incorrectWord: sentence.slice(start, end),
            correctedWord: null,
          });
        });
      });

      setErrorList(list);
    }
  }, []);

  const currentError = errorList[errorIndex];

  const applyCorrection = (correction) => {
    const updatedList = [...errorList];
    updatedList[errorIndex].correctedWord = correction;
    setErrorList(updatedList);
    setManualEdit(correction);
  };

  const handleNext = () => {
    if (manualEdit) {
      const key = currentError.originalSentence;
      const corrected = currentError.originalSentence.slice(0, currentError.start)
        + manualEdit +
        currentError.originalSentence.slice(currentError.end);

      setEditedSentences(prev => ({
        ...prev,
        [key]: corrected,
      }));
    }

    if (errorIndex < errorList.length - 1) {
      setErrorIndex(prev => prev + 1);
      setManualEdit("");
    } else {
      localStorage.setItem("editedJson", JSON.stringify(editedSentences));
      navigate("/all-error-corrected", {
        state: {
          selectedDocument,
          lastErrorIndex: errorList.length - 1,
        },
      });
    }
  };

  const handleBack = () => {
    if (errorIndex > 0) {
      setErrorIndex(prev => prev - 1);
    } else {
      navigate("/error-process", { state: { selectedDocument } });
    }
  };

  const handleSkip = () => {
    handleNext();
  };

  const getSentenceWithHighlight = (entry, index) => {
    const { originalSentence, start, end, correctedWord, incorrectWord } = entry;
    const isCurrent = index === errorIndex;
    const before = originalSentence.slice(0, start);
    const target = originalSentence.slice(start, end);
    const after = originalSentence.slice(end);

    let replacement = target;
    let color = "red";

    if (index < errorIndex) {
      replacement = correctedWord || target;
      color = "green";
    } else if (index > errorIndex) {
      color = "orange";
    }

    return (
      <p key={index} style={{ fontSize: "24px", lineHeight: "1.6" }}>
        {before}
        <span style={{ color, fontWeight: "bold" }}>
          {index === errorIndex
            ? target
            : replacement}
        </span>
        {after}
      </p>
    );
  };

  const getVisibleContext = () => {
    const context = [];
    if (errorList[errorIndex - 1]) context.push(getSentenceWithHighlight(errorList[errorIndex - 1], errorIndex - 1));
    context.push(getSentenceWithHighlight(errorList[errorIndex], errorIndex));
    if (errorList[errorIndex + 1]) context.push(getSentenceWithHighlight(errorList[errorIndex + 1], errorIndex + 1));
    return context;
  };

  if (!currentError) return null;

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "50px",
        }}
      >
        Edit Document
      </h1>

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: "30px",
          color: "#50464E",
          marginBottom: "50px",
        }}
      >
        <p>Document: {selectedDocument}</p>
        <p>
          {errorIndex + 1}/{errorList.length} current error
        </p>
      </div>

      <div style={{ display: "flex", gap: "50px" }}>
        {/* Left: Visible surrounding sentences with color highlights */}
        <div
          style={{
            flex: 1,
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            fontSize: "24px",
            lineHeight: "1.6",
            overflowWrap: "break-word",
          }}
        >
          {getVisibleContext()}
        </div>

        {/* Right: Editing panel */}
        <div style={{ flex: 1, fontSize: "30px", color: "#50464E" }}>
          <p>
            Error:{" "}
            <strong>
              {currentError.incorrectWord}
            </strong>
          </p>
          <p>Suggested words:</p>
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {currentError.suggestions.map((word, idx) => (
              <label key={idx} style={{ cursor: "pointer" }}>
                <input
                  type="radio"
                  name="suggestion"
                  onClick={() => applyCorrection(word)}
                  style={{ marginRight: "10px" }}
                />
                {word}
              </label>
            ))}
            <label style={{ cursor: "pointer" }}>
              <input type="radio" name="suggestion" style={{ marginRight: "10px" }} />
              Manual edit:
              <input
                type="text"
                value={manualEdit}
                onChange={(e) => setManualEdit(e.target.value)}
                onBlur={() => applyCorrection(manualEdit)}
                style={{
                  marginLeft: "10px",
                  padding: "5px 10px",
                  borderRadius: "10px",
                  border: "1px solid #A6785E",
                }}
              />
            </label>
            <div style={{ marginTop: "10px", textAlign: "right" }}>
              <button
                onClick={handleSkip}
                style={{
                  fontSize: "20px",
                  backgroundColor: "#DEA93D",
                  color: "#50464E",
                  padding: "8px 20px",
                  borderRadius: "8px",
                  border: "none",
                  cursor: "pointer",
                  fontWeight: "bold",
                }}
              >
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation buttons */}
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          gap: "25px",
          marginTop: "50px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleBack}
        >
          Back
        </button>
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
