import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || {};

  const [errorIndex, setErrorIndex] = useState(0);
  const [errorList, setErrorList] = useState([]);
  const [manualEdit, setManualEdit] = useState("");
  const [editedSentences, setEditedSentences] = useState({});

  useEffect(() => {
    const jsonString = localStorage.getItem("uploadedJson");
    if (jsonString) {
      const json = JSON.parse(jsonString);
      const list = [];

      Object.entries(json).forEach(([sentence, errors]) => {
        errors.forEach(([start, end, suggestions]) => {
          list.push({
            originalSentence: sentence,
            start,
            end,
            suggestions,
            incorrectWord: sentence.slice(start, end),
            correctedWord: null,
          });
        });
      });

      setErrorList(list);
    }
  }, []);

  const currentError = errorList[errorIndex];

  const applyCorrection = (correction) => {
    const updatedList = [...errorList];
    updatedList[errorIndex].correctedWord = correction;
    setErrorList(updatedList);
    setManualEdit(correction);
  };

  const handleNext = () => {
    if (manualEdit) {
      const entry = currentError;
      const fullSentence = editedSentences[entry.originalSentence] || entry.originalSentence;
      const newSentence = fullSentence.slice(0, entry.start) + manualEdit + fullSentence.slice(entry.end);
      setEditedSentences((prev) => ({
        ...prev,
        [entry.originalSentence]: newSentence,
      }));
    }

    if (errorIndex < errorList.length - 1) {
      setErrorIndex((prev) => prev + 1);
      setManualEdit("");
    } else {
      localStorage.setItem("editedJson", JSON.stringify(editedSentences));
      navigate("/all-error-corrected", {
        state: {
          selectedDocument,
          lastErrorIndex: errorList.length - 1,
        },
      });
    }
  };

  const handleBack = () => {
    if (errorIndex > 0) {
      setErrorIndex((prev) => prev - 1);
    } else {
      navigate("/error-process", { state: { selectedDocument } });
    }
  };

  const handleSkip = () => {
    handleNext();
  };

  const getHighlightedSentence = (sentence, sentenceErrors) => {
    let output = [];
    let lastIndex = 0;

    sentenceErrors.forEach(({ start, end, incorrectWord, correctedWord }, idx) => {
      output.push(sentence.slice(lastIndex, start));

      let word = sentence.slice(start, end);
      let style = { color: "red", fontWeight: "bold" };

      if (errorList.findIndex(e => e.originalSentence === sentence && e.start === start && e.end === end) < errorIndex) {
        style.color = "green";
        word = correctedWord || word;
      } else if (errorList[errorIndex]?.start === start && errorList[errorIndex]?.originalSentence === sentence) {
        style.color = "red";
      } else {
        style.color = "orange";
      }

      output.push(<span key={idx} style={style}>{word}</span>);
      lastIndex = end;
    });

    output.push(sentence.slice(lastIndex));
    return output;
  };

  const getVisibleContext = () => {
    const grouped = {};
    errorList.forEach((entry, idx) => {
      const key = entry.originalSentence;
      if (!grouped[key]) grouped[key] = [];
      grouped[key].push({
        ...entry,
        correctedWord: entry.correctedWord,
      });
    });

    const currentSentence = currentError.originalSentence;
    const displayed = Object.entries(grouped)
      .filter(([sentence]) => sentence === currentSentence)
      .map(([sentence, errors], idx) => (
        <p key={idx} style={{ fontSize: "24px", lineHeight: "1.6" }}>
          {getHighlightedSentence(
            editedSentences[sentence] || sentence,
            errors
          )}
        </p>
      ));

    return displayed;
  };

  if (!currentError) return null;

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "50px",
        }}
      >
        Edit Document
      </h1>

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: "30px",
          color: "#50464E",
          marginBottom: "50px",
        }}
      >
        <p>Document: {selectedDocument}</p>
        <p>
          {errorIndex + 1}/{errorList.length} current error
        </p>
      </div>

      <div style={{ display: "flex", gap: "50px" }}>
        <div
          style={{
            flex: 1,
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            fontSize: "24px",
            lineHeight: "1.6",
            overflowWrap: "break-word",
          }}
        >
          {getVisibleContext()}
        </div>

        <div style={{ flex: 1, fontSize: "30px", color: "#50464E" }}>
          <p>
            Error: <strong>{currentError.incorrectWord}</strong>
          </p>
          <p>Suggested words:</p>
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {currentError.suggestions.map((word, idx) => (
              <label key={idx} style={{ cursor: "pointer" }}>
                <input
                  type="radio"
                  name="suggestion"
                  onClick={() => applyCorrection(word)}
                  style={{ marginRight: "10px" }}
                />
                {word}
              </label>
            ))}
            <label style={{ cursor: "pointer" }}>
              <input type="radio" name="suggestion" style={{ marginRight: "10px" }} />
              Manual edit:
              <input
                type="text"
                value={manualEdit}
                onChange={(e) => setManualEdit(e.target.value)}
                onBlur={() => applyCorrection(manualEdit)}
                style={{
                  marginLeft: "10px",
                  padding: "5px 10px",
                  borderRadius: "10px",
                  border: "1px solid #A6785E",
                }}
              />
            </label>
            <div style={{ marginTop: "10px", textAlign: "right" }}>
              <button
                onClick={handleSkip}
                style={{
                  fontSize: "20px",
                  backgroundColor: "#DEA93D",
                  color: "#50464E",
                  padding: "8px 20px",
                  borderRadius: "8px",
                  border: "none",
                  cursor: "pointer",
                  fontWeight: "bold",
                }}
              >
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          gap: "25px",
          marginTop: "50px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleBack}
        >
          Back
        </button>
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
