import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || {};

  const [errorIndex, setErrorIndex] = useState(0);
  const [errorList, setErrorList] = useState([]);
  const [manualEdit, setManualEdit] = useState("");

  useEffect(() => {
    const jsonString = localStorage.getItem("uploadedJson");
    if (jsonString) {
      const json = JSON.parse(jsonString);
      const list = [];

      Object.entries(json).forEach(([sentence, errors]) => {
        errors.forEach(([start, end, suggestions]) => {
          list.push({
            sentence,
            start,
            end,
            suggestions,
            incorrectWord: sentence.slice(start, end),
          });
        });
      });

      setErrorList(list);
    }
  }, []);

  const currentError = errorList[errorIndex];
  const correctedErrors = errorIndex + 1;

  const highlightEntireDocument = () => {
    const originalJson = JSON.parse(localStorage.getItem("uploadedJson"));
    let highlighted = "";

    Object.entries(originalJson).forEach(([sentence, errors], idx) => {
      let result = sentence;
      let offset = 0;

      errors.forEach(([start, end], errIdx) => {
        const errorWord = sentence.slice(start, end);
        const isCurrent = errorList[errorIndex]?.sentence === sentence && errorList[errorIndex]?.start === start;

        let color = "orange";
        const indexInList = errorList.findIndex(e => e.sentence === sentence && e.start === start);

        if (indexInList < errorIndex) color = "green";
        else if (indexInList === errorIndex) color = "red";

        const colored = `<span style="color:${color}; font-weight:bold;">${errorWord}</span>`;
        result =
          result.slice(0, start + offset) +
          colored +
          result.slice(end + offset);
        offset += colored.length - (end - start);
      });

      highlighted += result + " ";
    });

    return highlighted;
  };

  const handleNext = () => {
    if (errorIndex < errorList.length - 1) {
      setErrorIndex((prev) => prev + 1);
      setManualEdit("");
    } else {
      navigate("/all-error-corrected", {
        state: {
          selectedDocument,
          lastErrorIndex: errorList.length - 1,
        },
      });
    }
  };

  const handleBack = () => {
    if (errorIndex > 0) {
      setErrorIndex((prev) => prev - 1);
    } else {
      navigate("/error-process", { state: { selectedDocument } });
    }
  };

  const handleSkip = () => {
    handleNext();
  };

  if (!currentError) return null;

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "50px",
        }}
      >
        Edit Document
      </h1>

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: "30px",
          color: "#50464E",
          marginBottom: "50px",
        }}
      >
        <p>Document: {selectedDocument}</p>
        <p>
          {correctedErrors}/{errorList.length} current error
        </p>
      </div>

      <div style={{ display: "flex", gap: "50px" }}>
        <div
          style={{
            flex: 1,
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            fontSize: "24px",
            lineHeight: "1.6",
            overflowY: "auto",
            maxHeight: "500px",
          }}
        >
          <p dangerouslySetInnerHTML={{ __html: highlightEntireDocument() }} />
        </div>

        <div style={{ flex: 1, fontSize: "30px", color: "#50464E" }}>
          <p>
            Error:{" "}
            <strong>
              {currentError.incorrectWord}
            </strong>
          </p>
          <p>Suggested words:</p>
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {currentError.suggestions.map((word, idx) => (
              <label key={idx} style={{ cursor: "pointer" }}>
                <input
                  type="radio"
                  name="suggestion"
                  onClick={() => setManualEdit(word)}
                  style={{ marginRight: "10px" }}
                />
                {word}
              </label>
            ))}
            <label style={{ cursor: "pointer" }}>
              <input type="radio" name="suggestion" style={{ marginRight: "10px" }} />
              Manual edit:
              <input
                type="text"
                value={manualEdit}
                onChange={(e) => setManualEdit(e.target.value)}
                style={{
                  marginLeft: "10px",
                  padding: "5px 10px",
                  borderRadius: "10px",
                  border: "1px solid #A6785E",
                }}
              />
            </label>
            <div style={{ marginTop: "10px", textAlign: "right" }}>
              <button
                onClick={handleSkip}
                style={{
                  fontSize: "20px",
                  backgroundColor: "#DEA93D",
                  color: "#50464E",
                  padding: "8px 20px",
                  borderRadius: "8px",
                  border: "none",
                  cursor: "pointer",
                  fontWeight: "bold",
                }}
              >
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          gap: "25px",
          marginTop: "50px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleBack}
        >
          Back
        </button>
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
