import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || {};

  const [errorIndex, setErrorIndex] = useState(0);
  const [groupedErrors, setGroupedErrors] = useState([]);
  const [manualEdit, setManualEdit] = useState("");
  const [allCorrections, setAllCorrections] = useState({}); // sentence => array of edits

  useEffect(() => {
    const jsonString = localStorage.getItem("uploadedJson");
    if (!jsonString) return;

    const json = JSON.parse(jsonString);
    const result = [];

    for (const [sentence, errorArr] of Object.entries(json)) {
      if (errorArr.length === 0) continue;

      const grouped = errorArr.map(([start, end, suggestions]) => ({
        start,
        end,
        suggestions,
        incorrectWord: sentence.slice(start, end),
        correctedWord: null,
      }));

      result.push({
        sentence,
        errors: grouped,
      });
    }

    setGroupedErrors(result);
  }, []);

  const handleApplyCorrection = (correction) => {
    const updated = [...groupedErrors];
    const currentGroup = updated[errorIndex];
    const currentErrorList = currentGroup.errors;

    const currentErrorIdx = currentErrorList.findIndex(e => e.correctedWord === null);
    if (currentErrorIdx === -1) return;

    currentErrorList[currentErrorIdx].correctedWord = correction;

    setGroupedErrors(updated);
    setManualEdit("");

    // Save the corrected sentence for export
    const correctedSentence = buildCorrectedSentence(currentGroup);
    setAllCorrections(prev => ({
      ...prev,
      [currentGroup.sentence]: correctedSentence,
    }));
  };

  const handleNext = () => {
    const currentGroup = groupedErrors[errorIndex];
    const hasUncorrected = currentGroup.errors.some(e => !e.correctedWord);

    if (manualEdit && hasUncorrected) {
      handleApplyCorrection(manualEdit);
      return;
    }

    if (errorIndex < groupedErrors.length - 1) {
      setErrorIndex(prev => prev + 1);
    } else {
      localStorage.setItem("editedJson", JSON.stringify(allCorrections));
      navigate("/all-error-corrected", {
        state: {
          selectedDocument,
          lastErrorIndex: errorIndex,
        },
      });
    }
  };

  const handleBack = () => {
    if (errorIndex > 0) {
      setErrorIndex(prev => prev - 1);
    } else {
      navigate("/error-process", { state: { selectedDocument } });
    }
  };

  const handleSkip = () => {
    handleNext();
  };

  const buildCorrectedSentence = (entry) => {
    const { sentence, errors } = entry;
    let result = "";
    let lastIndex = 0;

    errors
      .sort((a, b) => a.start - b.start)
      .forEach(({ start, end, correctedWord }) => {
        result += sentence.slice(lastIndex, start);
        result += correctedWord ?? sentence.slice(start, end);
        lastIndex = end;
      });

    result += sentence.slice(lastIndex);
    return result;
  };

  const getHighlightedSentence = (entry, index) => {
    const { sentence, errors } = entry;
    let result = "";
    let lastIndex = 0;

    const sorted = errors.sort((a, b) => a.start - b.start);

    sorted.forEach((err, idx) => {
      const { start, end, correctedWord, incorrectWord } = err;
      const before = sentence.slice(lastIndex, start);
      const original = sentence.slice(start, end);
      const isPast = index < errorIndex;
      const isCurrent = index === errorIndex && correctedWord === null;
      const isFuture = index > errorIndex || (index === errorIndex && idx > 0);

      let display = original;
      let color = "red";

      if (isPast) {
        display = correctedWord || original;
        color = "green";
      } else if (isCurrent) {
        color = "red";
      } else if (isFuture) {
        color = "orange";
      }

      result += before + `<span style='color:${color}; font-weight:bold;'>${display}</span>`;
      lastIndex = end;
    });

    result += sentence.slice(lastIndex);
    return result;
  };

  if (!groupedErrors[errorIndex]) return null;

  const current = groupedErrors[errorIndex];
  const nextError = current.errors.find(e => e.correctedWord === null) || current.errors[0];

  return (
    <div style={{ backgroundColor: "#F0DFC3", fontFamily: "Proxima Nova", minHeight: "100vh", padding: "50px 100px", display: "flex", flexDirection: "column" }}>
      <h1 style={{ fontSize: "50px", fontWeight: "bold", color: "#A6785E", textAlign: "center", marginBottom: "50px" }}>Edit Document</h1>

      <div style={{ display: "flex", justifyContent: "space-between", fontSize: "30px", color: "#50464E", marginBottom: "50px" }}>
        <p>Document: {selectedDocument}</p>
        <p>
          {errorIndex + 1}/{groupedErrors.length} current error
        </p>
      </div>

      <div style={{ display: "flex", gap: "50px" }}>
        <div style={{ flex: 1, background: "white", padding: "20px", borderRadius: "10px", fontSize: "24px", lineHeight: "1.6", overflowWrap: "break-word" }}>
          <p dangerouslySetInnerHTML={{ __html: getHighlightedSentence(current, errorIndex) }}></p>
        </div>

        <div style={{ flex: 1, fontSize: "30px", color: "#50464E" }}>
          <p>
            Error: <strong>{nextError?.incorrectWord}</strong>
          </p>
          <p>Suggested words:</p>
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {nextError?.suggestions.map((word, idx) => (
              <label key={idx} style={{ cursor: "pointer" }}>
                <input
                  type="radio"
                  name="suggestion"
                  onClick={() => handleApplyCorrection(word)}
                  style={{ marginRight: "10px" }}
                />
                {word}
              </label>
            ))}
            <label style={{ cursor: "pointer" }}>
              <input type="radio" name="suggestion" style={{ marginRight: "10px" }} />
              Manual edit:
              <input
                type="text"
                value={manualEdit}
                onChange={(e) => setManualEdit(e.target.value)}
                onBlur={() => handleApplyCorrection(manualEdit)}
                style={{
                  marginLeft: "10px",
                  padding: "5px 10px",
                  borderRadius: "10px",
                  border: "1px solid #A6785E",
                }}
              />
            </label>
            <div style={{ marginTop: "10px", textAlign: "right" }}>
              <button
                onClick={handleSkip}
                style={{
                  fontSize: "20px",
                  backgroundColor: "#DEA93D",
                  color: "#50464E",
                  padding: "8px 20px",
                  borderRadius: "8px",
                  border: "none",
                  cursor: "pointer",
                  fontWeight: "bold",
                }}
              >
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: "flex", justifyContent: "flex-end", gap: "25px", marginTop: "50px" }}>
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleBack}
        >
          Back
        </button>
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
