import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || { selectedDocument: "uploaded.json" };

  const [errorIndex, setErrorIndex] = useState(0);
  const [errorList, setErrorList] = useState([]);
  const [fixMap, setFixMap] = useState({}); // { "sentence": [[start, end, fixedWord]] }
  const [manualEdit, setManualEdit] = useState("");

  // Load document & build error list
  useEffect(() => {
    const data = localStorage.getItem("uploadedJson");
    if (!data) return;
    const parsed = JSON.parse(data);

    const tempList = [];

    Object.entries(parsed).forEach(([sentence, errors]) => {
      errors.forEach((error, idx) => {
        const [start, end, suggestions] = error;
        const incorrectWord = sentence.slice(start, end);
        tempList.push({
          sentence,
          index: idx,
          incorrectWord,
          suggestions,
          start,
          end,
        });
      });
    });

    setErrorList(tempList);
  }, []);

  const totalErrors = errorList.length;
  const currentError = errorList[errorIndex];

  const handleNext = () => {
    if (manualEdit.trim() !== "") {
      const prev = fixMap[currentError.sentence] || [];
      const updated = [...prev, [currentError.start, currentError.end, manualEdit.trim()]];
      setFixMap((prevMap) => ({ ...prevMap, [currentError.sentence]: updated }));
    }

    setManualEdit("");
    if (errorIndex < totalErrors - 1) {
      setErrorIndex((prev) => prev + 1);
    } else {
      localStorage.setItem("fixedJson", JSON.stringify(fixMap));
      navigate("/all-error-corrected", { state: { selectedDocument, lastErrorIndex: errorIndex } });
    }
  };

  const handleBack = () => {
    if (errorIndex > 0) {
      setErrorIndex((prev) => prev - 1);
    } else {
      navigate("/error-process", { state: { selectedDocument } });
    }
  };

  const handleSkip = () => {
    setManualEdit("");
    if (errorIndex < totalErrors - 1) {
      setErrorIndex((prev) => prev + 1);
    } else {
      navigate("/all-error-corrected", { state: { selectedDocument, lastErrorIndex: errorIndex } });
    }
  };

  const highlightSentence = (sentence, currentStart, currentEnd) => {
    const fixed = fixMap[sentence] || [];
    const allErrorSpots = errorList
      .filter((e, i) => e.sentence === sentence)
      .map((e, i) => ({ ...e, idx: i }));

    let html = "";
    let last = 0;

    const segments = [];

    allErrorSpots.forEach((e) => {
      const start = e.start;
      const end = e.end;
      const fixedWord = fixed.find(([s]) => s === start)?.[2];
      const color =
        e.start === currentStart && e.end === currentEnd
          ? "red"
          : fixedWord
          ? "green"
          : "orange";

      const word = fixedWord || sentence.slice(start, end);

      if (start > last) {
        segments.push(sentence.slice(last, start));
      }

      segments.push(
        `<span style='color:${color}; font-weight:bold;'>${word}</span>`
      );
      last = end;
    });

    if (last < sentence.length) {
      segments.push(sentence.slice(last));
    }

    return segments.join("");
  };

  if (errorList.length === 0) return <div>Loading...</div>;

  const contextWindow = 2;
  const contextStart = Math.max(0, errorIndex - contextWindow);
  const contextEnd = Math.min(errorList.length, errorIndex + contextWindow + 1);
  const visibleSentences = Array.from(
    new Set(errorList.slice(contextStart, contextEnd).map((e) => e.sentence))
  );

  return (
    <div style={{ backgroundColor: "#F0DFC3", fontFamily: "Proxima Nova", minHeight: "100vh", padding: "50px 100px", display: "flex", flexDirection: "column" }}>
      <h1 style={{ fontSize: "50px", fontWeight: "bold", color: "#A6785E", textAlign: "center", marginBottom: "40px" }}>
        Edit Document
      </h1>

      <div style={{ display: "flex", justifyContent: "space-between", fontSize: "28px", color: "#50464E", marginBottom: "30px" }}>
        <p>Document: {selectedDocument}</p>
        <p>{errorIndex + 1}/{totalErrors} current error</p>
      </div>

      <div style={{ display: "flex", gap: "50px" }}>
        <div style={{ flex: 1, background: "white", padding: "20px", borderRadius: "10px", fontSize: "24px", lineHeight: "1.8" }}>
          {visibleSentences.map((sentence, i) => {
            const curr = errorList[errorIndex];
            return (
              <p
                key={i}
                dangerouslySetInnerHTML={{
                  __html: highlightSentence(sentence, curr.start, curr.end),
                }}
              />
            );
          })}
        </div>

        <div style={{ flex: 1, fontSize: "28px", color: "#50464E" }}>
          <p>Error: <strong>{currentError.incorrectWord}</strong></p>
          <p>Suggested words:</p>
          <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            {currentError.suggestions.map((word, idx) => (
              <label key={idx} style={{ cursor: "pointer" }}>
                <input
                  type="radio"
                  name="suggestion"
                  onClick={() => setManualEdit(word)}
                  style={{ marginRight: "10px" }}
                />
                {word}
              </label>
            ))}
            <label>
              <input type="radio" name="suggestion" />
              Manual edit:
              <input
                type="text"
                value={manualEdit}
                onChange={(e) => setManualEdit(e.target.value)}
                style={{ marginLeft: "10px", padding: "5px 10px", borderRadius: "10px", border: "1px solid #A6785E" }}
              />
            </label>
            <div style={{ marginTop: "15px", textAlign: "right" }}>
              <button
                onClick={handleSkip}
                style={{ fontSize: "20px", backgroundColor: "#DEA93D", color: "#50464E", padding: "8px 20px", borderRadius: "8px", border: "none", cursor: "pointer", fontWeight: "bold" }}
              >
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: "flex", justifyContent: "flex-end", gap: "25px", marginTop: "50px" }}>
        <button
          style={{ fontSize: "30px", backgroundColor: "#DEA93D", color: "#50464E", padding: "10px 30px", borderRadius: "10px", border: "none", cursor: "pointer", fontWeight: "bold" }}
          onClick={handleBack}
        >
          Back
        </button>
        <button
          style={{ fontSize: "30px", backgroundColor: "#DEA93D", color: "#50464E", padding: "10px 30px", borderRadius: "10px", border: "none", cursor: "pointer", fontWeight: "bold" }}
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
