import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || { selectedDocument: "uploaded.json" };

  const [errorList, setErrorList] = useState([]);
  const [currentErrorIndex, setCurrentErrorIndex] = useState(0);
  const [fixMap, setFixMap] = useState({});
  const [manualEdit, setManualEdit] = useState("");

  useEffect(() => {
    const json = JSON.parse(localStorage.getItem("editedJson"));
    if (!json) return;

    const list = [];
    let sentenceIndex = 0;

    for (const [text, errors] of Object.entries(json)) {
      errors.forEach((error, idx) => {
        const [start, end, suggestions] = error;
        const incorrectWord = text.slice(start, end);

        list.push({
          sentenceIndex,
          originalText: text,
          incorrectWord,
          start,
          end,
          suggestions,
        });
      });
      sentenceIndex++;
    }

    setErrorList(list);
  }, [selectedDocument]);

  const currentError = errorList[currentErrorIndex];
  const totalErrors = errorList.length;

  const applyHighlights = (text, sentenceIndex) => {
    let highlighted = "";
    let cursor = 0;
    const currentFixes = errorList
      .filter((e, i) => i !== currentErrorIndex && fixMap[i] && e.sentenceIndex === sentenceIndex)
      .map((e, i) => ({
        start: e.start,
        end: e.end,
        replacement: fixMap[i],
        type: "fixed"
      }));

    const futureErrors = errorList
      .filter((e, i) => i > currentErrorIndex && e.sentenceIndex === sentenceIndex && !fixMap[i])
      .map(e => ({
        start: e.start,
        end: e.end,
        replacement: e.originalText.slice(e.start, e.end),
        type: "future"
      }));

    const isCurrent = currentError?.sentenceIndex === sentenceIndex && !fixMap[currentErrorIndex];
    const current = isCurrent ? [{
      start: currentError.start,
      end: currentError.end,
      replacement: currentError.originalText.slice(currentError.start, currentError.end),
      type: "current"
    }] : [];

    const allEdits = [...currentFixes, ...futureErrors, ...current].sort((a, b) => a.start - b.start);

    allEdits.forEach(edit => {
      highlighted += text.slice(cursor, edit.start);
      highlighted += `<span style="font-weight:bold; color:${
        edit.type === "fixed" ? "green" :
        edit.type === "future" ? "orange" :
        "red"
      }">${edit.replacement}</span>`;
      cursor = edit.end;
    });

    highlighted += text.slice(cursor);
    return highlighted;
  };

  const handleNext = () => {
    if (manualEdit.trim() !== "") {
      setFixMap(prev => ({
        ...prev,
        [currentErrorIndex]: manualEdit.trim()
      }));
    }
    setManualEdit("");
    setCurrentErrorIndex(prev => prev + 1);
  };

  const handleBack = () => {
    if (currentErrorIndex === 0) {
      navigate("/error-process", { state: { selectedDocument } });
    } else {
      setCurrentErrorIndex(prev => prev - 1);
    }
  };

  const handleSkip = () => {
    setManualEdit("");
    setCurrentErrorIndex(prev => prev + 1);
  };

  if (!currentError) return null;

  const sentenceGroup = errorList
    .map(e => e.originalText)
    .filter((val, idx, self) => self.indexOf(val) === idx)
    .slice(
      Math.max(0, currentError.sentenceIndex - 1),
      currentError.sentenceIndex + 2
    );

  return (
    <div style={{ backgroundColor: "#F0DFC3", fontFamily: "Proxima Nova", minHeight: "100vh", padding: "50px 100px" }}>
      <h1 style={{ fontSize: "50px", fontWeight: "bold", color: "#A6785E", textAlign: "center", marginBottom: "50px" }}>Edit Document</h1>

      <div style={{ display: "flex", justifyContent: "space-between", fontSize: "30px", color: "#50464E", marginBottom: "30px" }}>
        <p>Document: {selectedDocument}</p>
        <p>{currentErrorIndex + 1}/{totalErrors} current error</p>
      </div>

      <div style={{ display: "flex", gap: "40px" }}>
        {/* Text */}
        <div style={{ flex: 1, background: "white", padding: "20px", borderRadius: "10px", fontSize: "24px", lineHeight: "1.6" }}>
          {sentenceGroup.map((line, idx) => (
            <p
              key={idx}
              dangerouslySetInnerHTML={{
                __html: applyHighlights(line, currentError.sentenceIndex - 1 + idx)
              }}
            />
          ))}
        </div>

        {/* Right Panel */}
        <div style={{ flex: 1, fontSize: "28px", color: "#50464E" }}>
          <p><strong>Error:</strong> {currentError.incorrectWord}</p>
          <p><strong>Suggestions:</strong></p>

          <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            {currentError.suggestions.map((word, idx) => (
              <label key={idx}>
                <input
                  type="radio"
                  name="suggestion"
                  onChange={() => setManualEdit(word)}
                  style={{ marginRight: "10px" }}
                />
                {word}
              </label>
            ))}

            <label style={{ marginTop: "15px" }}>
              <input type="radio" name="suggestion" />
              Manual edit:
              <input
                type="text"
                value={manualEdit}
                onChange={(e) => setManualEdit(e.target.value)}
                style={{ marginLeft: "10px", padding: "5px 10px", borderRadius: "8px", border: "1px solid #A6785E" }}
              />
            </label>

            <div style={{ marginTop: "15px", textAlign: "right" }}>
              <button
                onClick={handleSkip}
                style={{ fontSize: "20px", backgroundColor: "#DEA93D", color: "#50464E", padding: "6px 18px", borderRadius: "6px", border: "none", fontWeight: "bold", cursor: "pointer" }}
              >
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Buttons */}
      <div style={{ display: "flex", justifyContent: "flex-end", gap: "20px", marginTop: "40px" }}>
        <button
          onClick={handleBack}
          style={{ fontSize: "28px", backgroundColor: "#DEA93D", color: "#50464E", padding: "10px 25px", borderRadius: "10px", border: "none", fontWeight: "bold", cursor: "pointer" }}
        >
          Back
        </button>

        <button
          onClick={handleNext}
          style={{ fontSize: "28px", backgroundColor: "#DEA93D", color: "#50464E", padding: "10px 25px", borderRadius: "10px", border: "none", fontWeight: "bold", cursor: "pointer" }}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
