import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../../App.css";

const EditingHistoryPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const selectedDocument = location.state?.selectedDocument || "No document selected";

  const [edits, setEdits] = useState([]);

  useEffect(() => {
    const log = JSON.parse(localStorage.getItem("editingLog") || "{}");
    setEdits(log[selectedDocument] || []);
  }, [selectedDocument]);

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        position: "relative",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "40px",
        }}
      >
        Editing History
      </h1>

      <h2
        style={{
          fontSize: "35px",
          fontWeight: "bold",
          color: "#50464E",
          textAlign: "center",
          marginBottom: "30px",
        }}
      >
        {selectedDocument}
      </h2>

      <div
        style={{
          fontSize: "28px",
          color: "#50464E",
          marginBottom: "50px",
        }}
      >
        {edits.length > 0 ? (
          edits.map((edit, index) => (
            <p key={index}>
              Line {edit.line}: “{edit.from}” to “{edit.to}”
            </p>
          ))
        ) : (
          <p style={{ fontStyle: "italic", color: "#888" }}>
            No edits have been made to this document yet.
          </p>
        )}
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          gap: "25px",
          marginTop: "auto",
        }}
      >
        <button
          style={buttonStyle}
          onClick={() => navigate("/")}
        >
          Home
        </button>

        <button
          style={buttonStyle}
          onClick={() => navigate("/all-documents")}
        >
          Back
        </button>
      </div>
    </div>
  );
};

const buttonStyle = {
  fontSize: "30px",
  backgroundColor: "#DEA93D",
  color: "#50464E",
  padding: "10px 30px",
  borderRadius: "10px",
  border: "none",
  cursor: "pointer",
  fontWeight: "bold",
};

export default EditingHistoryPage;
