import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditingHistoryPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || {};

  const [edits, setEdits] = useState([]);

  useEffect(() => {
    const original = JSON.parse(localStorage.getItem("originalJson") || "{}");
    const edited = JSON.parse(localStorage.getItem("editedJson") || "{}");

    const changes = [];

    Object.keys(original).forEach((line, idx) => {
      const editedLine = edited[line];
      if (editedLine && editedLine !== line) {
        const diff = getReplacements(line, editedLine);
        diff.forEach(({ start, end, replacement }) => {
          changes.push(`Line ${idx + 1}: “${line.slice(start, end)}” to “${replacement}”`);
        });
      }
    });

    setEdits(changes);
  }, []);

  const getReplacements = (original, edited) => {
    const replacements = [];
    let i = 0;

    while (i < original.length) {
      if (original[i] !== edited[i]) {
        let start = i;
        while (i < original.length && original[i] !== edited[i]) {
          i++;
        }
        let end = i;
        replacements.push({
          start,
          end,
          replacement: edited.slice(start, end),
        });
      } else {
        i++;
      }
    }

    return replacements;
  };

  const half = Math.ceil(edits.length / 2);
  const left = edits.slice(0, half);
  const right = edits.slice(half);

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "30px",
        }}
      >
        Editing History
      </h1>

      <h2
        style={{
          fontSize: "35px",
          fontWeight: "bold",
          color: "#50464E",
          textAlign: "center",
          marginBottom: "30px",
        }}
      >
        {selectedDocument || "Document"}
      </h2>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: "25px",
          fontSize: "30px",
          color: "#50464E",
        }}
      >
        <div>{left.map((edit, index) => <p key={index}>{edit}</p>)}</div>
        <div>{right.map((edit, index) => <p key={index}>{edit}</p>)}</div>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          gap: "25px",
          marginTop: "40px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={() => navigate("/")}
        >
          Home
        </button>
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={() => navigate("/all-documents")}
        >
          Back
        </button>
      </div>
    </div>
  );
};

export default EditingHistoryPage;
