import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../App.css";

const DataStoragePage = () => {
  const navigate = useNavigate();
  const [uploadedDocs, setUploadedDocs] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("uploadedDocuments") || "[]");
    setUploadedDocs(stored);
  }, []);

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Editing History Section */}
      <div style={{ marginBottom: "50px" }}>
        <h1 style={sectionTitle}>Editing History</h1>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-between",
            fontSize: "30px",
            color: "#50464E",
            padding: "0 40px",
          }}
        >
          {uploadedDocs.slice(0, 2).map((doc, index) => (
            <div key={index} style={{ margin: "20px" }}>
              <p
                style={{ cursor: "pointer" }}
                onClick={() => navigate("/editing-history", { state: { selectedDocument: doc } })}
              >
                {doc}
              </p>
              <div style={{ marginLeft: "30px" }}>
                <p>Line 4: “...” to “...”</p>
                <p>Line 19: “...” to “...”</p>
                <p
                  style={{
                    color: "#A6785E",
                    cursor: "pointer",
                    fontStyle: "italic",
                    marginTop: "25px",
                    marginLeft: "20px",
                  }}
                  onClick={() => navigate("/editing-history", { state: { selectedDocument: doc } })}
                >
                  Show more edits
                </p>
              </div>
            </div>
          ))}
        </div>

        <p
          style={{
            textAlign: "center",
            fontSize: "30px",
            color: "#A6785E",
            cursor: "pointer",
            fontStyle: "italic",
            marginBottom: "50px",
          }}
          onClick={() => navigate("/all-documents")}
        >
          Show all
        </p>
      </div>

      {/* Uploaded Files Section */}
      <div style={{ marginBottom: "50px" }}>
        <h1 style={sectionTitle}>Uploaded Files</h1>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(4, 1fr)",
            justifyItems: "center",
            fontSize: "30px",
            color: "#50464E",
            marginBottom: "25px",
            columnGap: "120px",
          }}
        >
          {uploadedDocs.slice(0, 8).map((doc, index) => (
            <p key={index}>{doc}</p>
          ))}
        </div>
        <p
          style={{
            textAlign: "center",
            fontSize: "30px",
            color: "#A6785E",
            cursor: "pointer",
            fontStyle: "italic",
            marginBottom: "25px",
          }}
          onClick={() => navigate("/all-documents")}
        >
          Show all
        </p>
      </div>

      {/* Back Button */}
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginBottom: "25px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={() => navigate("/")}
        >
          Back
        </button>
      </div>
    </div>
  );
};

const sectionTitle = {
  fontSize: "50px",
  fontWeight: "bold",
  color: "#A6785E",
  textAlign: "center",
  marginBottom: "25px",
};

export default DataStoragePage;
