import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../App.css";

const EditErrorsPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || { selectedDocument: "Uploaded.json" };

  const [errorIndex, setErrorIndex] = useState(0);
  const [correctedErrors, setCorrectedErrors] = useState(1);
  const [manualEdit, setManualEdit] = useState("");
  const [errorList, setErrorList] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem("editedJson");
    if (stored) {
      const parsed = JSON.parse(stored);
      const list = [];

      let pastErrors = [];
      const entries = Object.entries(parsed);

      for (let i = 0; i < entries.length; i++) {
        const [text, errors] = entries[i];
        const futureErrors = entries.slice(i + 1).flatMap(([futureText, future]) =>
          future.map(([start, end]) => futureText.slice(start, end))
        );

        if (errors.length > 0) {
          const [start, end, suggestions] = errors[0]; // First error only for now
          const incorrectWord = text.slice(start, end);

          list.push({
            text,
            start,
            end,
            incorrectWord,
            suggestions,
            pastErrors: [...pastErrors],
            futureErrors,
          });

          pastErrors.push(incorrectWord);
        }
      }

      setErrorList(list);
    }
  }, []);

  const highlightText = ({ text, start, end, incorrectWord, pastErrors, futureErrors }) => {
    let result = text;

    // Highlight future first (so others overwrite it if overlapping)
    futureErrors.forEach((word) => {
      result = result.replace(new RegExp(`\\b${word}\\b`, "g"), `<span style='color:orange; font-weight:bold;'>${word}</span>`);
    });

    // Highlight past
    pastErrors.forEach((word) => {
      result = result.replace(new RegExp(`\\b${word}\\b`, "g"), `<span style='color:green; font-weight:bold;'>${word}</span>`);
    });

    // Highlight current
    result = result.replace(new RegExp(`\\b${incorrectWord}\\b`, "g"), `<span style='color:red; font-weight:bold;'>${incorrectWord}</span>`);

    return result;
  };

  const handleNextError = () => {
    if (errorIndex < errorList.length - 1) {
      setErrorIndex((prev) => prev + 1);
      setCorrectedErrors((prev) => prev + 1);
      setManualEdit("");
    } else {
      navigate("/all-error-corrected", { state: { selectedDocument, lastErrorIndex: errorList.length - 1 } });
    }
  };

  const handleBack = () => {
    if (errorIndex > 0) {
      setErrorIndex((prev) => prev - 1);
      setCorrectedErrors((prev) => prev - 1);
    } else {
      navigate("/error-process", { state: { selectedDocument } });
    }
  };

  const handleSkip = () => {
    if (errorIndex < errorList.length - 1) {
      setErrorIndex((prev) => prev + 1);
      setManualEdit("");
    } else {
      navigate("/all-error-corrected", { state: { selectedDocument, lastErrorIndex: errorList.length - 1 } });
    }
  };

  if (errorList.length === 0) return null;
  const current = errorList[errorIndex];

  return (
    <div style={{ backgroundColor: "#F0DFC3", fontFamily: "Proxima Nova", minHeight: "100vh", padding: "50px 100px", display: "flex", flexDirection: "column" }}>
      <h1 style={{ fontSize: "50px", fontWeight: "bold", color: "#A6785E", textAlign: "center", marginBottom: "50px" }}>Edit Document</h1>

      <div style={{ display: "flex", justifyContent: "space-between", fontSize: "30px", color: "#50464E", marginBottom: "50px" }}>
        <p>Document: {selectedDocument}</p>
        <p>{correctedErrors}/{errorList.length} current error</p>
      </div>

      <div style={{ display: "flex", gap: "50px" }}>
        <div style={{ flex: 1, background: "white", padding: "20px", borderRadius: "10px", fontSize: "24px", lineHeight: "1.6", overflowWrap: "break-word" }}>
          <p dangerouslySetInnerHTML={{ __html: highlightText(current) }}></p>
        </div>

        <div style={{ flex: 1, fontSize: "30px", color: "#50464E" }}>
          <p>Error: <strong>{current.incorrectWord}</strong></p>
          <p>Suggested words:</p>
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {current.suggestions.map((word, idx) => (
              <label key={idx} style={{ cursor: "pointer" }}>
                <input type="radio" name="suggestion" onClick={() => setManualEdit(word)} style={{ marginRight: "10px" }} />
                {word}
              </label>
            ))}
            <label style={{ cursor: "pointer" }}>
              <input type="radio" name="suggestion" style={{ marginRight: "10px" }} />
              Manual edit:
              <input type="text" value={manualEdit} onChange={(e) => setManualEdit(e.target.value)} style={{ marginLeft: "10px", padding: "5px 10px", borderRadius: "10px", border: "1px solid #A6785E" }} />
            </label>
            <div style={{ marginTop: "10px", textAlign: "right" }}>
              <button onClick={handleSkip} style={{ fontSize: "20px", backgroundColor: "#DEA93D", color: "#50464E", padding: "8px 20px", borderRadius: "8px", border: "none", cursor: "pointer", fontWeight: "bold" }}>
                Skip
              </button>
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: "flex", justifyContent: "flex-end", gap: "25px", marginTop: "50px" }}>
        <button onClick={handleBack} style={{ fontSize: "30px", backgroundColor: "#DEA93D", color: "#50464E", padding: "10px 30px", borderRadius: "10px", border: "none", cursor: "pointer", fontWeight: "bold" }}>Back</button>
        <button onClick={handleNextError} style={{ fontSize: "30px", backgroundColor: "#DEA93D", color: "#50464E", padding: "10px 30px", borderRadius: "10px", border: "none", cursor: "pointer", fontWeight: "bold" }}>Next</button>
      </div>
    </div>
  );
};

export default EditErrorsPage;
