import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../../App.css";

const EditingHistoryPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedDocument } = location.state || {};

  const [edits, setEdits] = useState([]);

  useEffect(() => {
    const originalJson = JSON.parse(localStorage.getItem("originalJson") || "{}");
    const editedJson = JSON.parse(localStorage.getItem("editedJson") || "{}");

    const changes = [];

    Object.keys(editedJson).forEach((line, index) => {
      const original = line;
      const edited = editedJson[line];

      if (original !== edited) {
        let diffStart = 0;
        while (
          diffStart < original.length &&
          original[diffStart] === edited[diffStart]
        ) {
          diffStart++;
        }

        let diffEndOriginal = original.length - 1;
        let diffEndEdited = edited.length - 1;

        while (
          diffEndOriginal > diffStart &&
          diffEndEdited > diffStart &&
          original[diffEndOriginal] === edited[diffEndEdited]
        ) {
          diffEndOriginal--;
          diffEndEdited--;
        }

        const originalWord = original.slice(diffStart, diffEndOriginal + 1);
        const editedWord = edited.slice(diffStart, diffEndEdited + 1);

        changes.push(
          `Line ${index + 1}: “${originalWord}” → “${editedWord}”`
        );
      }
    });

    setEdits(changes);
  }, []);

  const half = Math.ceil(edits.length / 2);
  const leftEdits = edits.slice(0, half);
  const rightEdits = edits.slice(half);

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "50px",
        }}
      >
        Editing History
      </h1>

      {selectedDocument && (
        <h2
          style={{
            fontSize: "35px",
            fontWeight: "bold",
            color: "#50464E",
            textAlign: "center",
            marginBottom: "30px",
          }}
        >
          {selectedDocument}
        </h2>
      )}

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: "25px",
          fontSize: "30px",
          color: "#50464E",
        }}
      >
        <div>
          {leftEdits.map((edit, index) => (
            <p key={index}>{edit}</p>
          ))}
        </div>
        <div>
          {rightEdits.map((edit, index) => (
            <p key={index}>{edit}</p>
          ))}
        </div>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          gap: "25px",
          marginTop: "25px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={() => navigate("/")}
        >
          Home
        </button>

        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={() => navigate("/all-documents")}
        >
          Back
        </button>
      </div>
    </div>
  );
};

export default EditingHistoryPage;
